from __future__ import annotationsimport mathimport commands2import wpimathimport wpilibimport typingfrom commands2 import cmd, InstantCommand, RunCommandfrom commands2.button import CommandGenericHIDfrom rev import LimitSwitchConfigfrom wpilib import XboxControllerfrom wpimath.controller import PIDController, ProfiledPIDControllerRadians, HolonomicDriveControllerfrom wpimath.geometry import Pose2d, Rotation2d, Translation2dfrom wpimath.trajectory import TrajectoryConfig, TrajectoryGeneratorfrom commands.intakecommands import IntakeFeedGamepieceForward, IntakeGamepiece, IntakeEjectGamepieceBackwardfrom constants import AutoConstants, DriveConstants, OIConstants, LiftConstants, IntakeConstantsfrom subsystems.drivesubsystem import DriveSubsystemfrom subsystems.elevator import Elevatorfrom subsystems.intake import Intakefrom subsystems.limelight_camera import LimelightCamerafrom subsystems.singleIntake import SingleIntakefrom commands.reset_xy import ResetXY, ResetSwerveFrontfrom commands.aimtodirection import AimToDirectionfrom commands.alignwithtag import AlignWithTagfrom commands.arcadedrive import ArcadeDrivefrom commands.elevatorposition import SetElevatorPositionfrom commands.findobject import FindObjectfrom commands.followobject import FollowObject, StopWhenfrom commands.gotopoint import GoToPointfrom commands.holonomicdrive import HolonomicDrivefrom commands.jerky_trajectory import JerkyTrajectoryfrom commands.reset_xy import ResetXYfrom commands.setcamerapipeline import SetCameraPipelinefrom commands.swervetopoint import SwerveToPointimport commands.intakecommandsclass RobotContainer:    """    This class is where the bulk of the robot should be declared. Since Command-based is a    "declarative" paradigm, very little robot logic should actually be handled in the :class:`.Robot`    periodic methods (other than the scheduler calls). Instead, the structure of the robot (including    subsystems, commands, and button mappings) should be declared here.    """    def __init__(self) -> None:        # The robot's subsystems        self.robotDrive = DriveSubsystem()        self.limelight = LimelightCamera("limelight")        self.limelight.setPiPMode(2)        self.intake = Intake(leaderCanID=IntakeConstants.kLeadIntake, followerCanID=IntakeConstants.kFollowIntake, leaderInverted=False, followerInverted=True, rangeFinder=None)        self.singleIntake = SingleIntake(intakeCanID=IntakeConstants.kIntake)        self.elevator = Elevator(leadMotorCANId=LiftConstants.kLeadLift, followMotorCANId=LiftConstants.kFollowLift,                                 presetSwitchPositions=(0.1, 4.333, 12.51))        # The driver's controller        self.driverController = CommandGenericHID(OIConstants.kDriverControllerPort)        self.operatorController = CommandGenericHID(OIConstants.kOperatorControllerPort)        # Configure the button bindings and autos        self.configureButtonBindings()        self.configureAutos()        # Configure default command for driving using sticks        self.robotDrive.setDefaultCommand(            HolonomicDrive(                self.robotDrive,                forwardSpeed=lambda: -self.driverController.getRawAxis(XboxController.Axis.kLeftY),                leftSpeed=lambda: -self.driverController.getRawAxis(XboxController.Axis.kLeftX),                rotationSpeed=lambda: -self.driverController.getRawAxis(XboxController.Axis.kRightX),                deadband=OIConstants.kDriveDeadband,                fieldRelative=True,                rateLimit=True,                square=True,            )        )        self.elevator.setDefaultCommand(            commands2.RunCommand(lambda: self.elevator.drive(self.operatorController.getRawAxis(XboxController.Axis.kLeftY)), self.elevator)        )    def configureButtonBindings(self) -> None:        """        Use this method to define your button->command mappings. Buttons can be created by        instantiating a :GenericHID or one of its subclasses (Joystick or XboxController),        and then passing it to a JoystickButton.        """        #Driver Controller        povUpDriverButton = self.driverController.pov(0)        povUpDriverButton.onTrue(ResetXY(x=0.0, y=0.0, headingDegrees=0.0, drivetrain=self.robotDrive))        povUpDriverButton.whileTrue(RunCommand(self.robotDrive.setX, self.robotDrive))        povDownDriverButton = self.driverController.pov(180)        povDownDriverButton.onTrue(ResetSwerveFront(self.robotDrive))        bDriverController = self.driverController.button(XboxController.Button.kB)        FeedUpper = IntakeFeedGamepieceForward(self.intake, motor1speed=-0.5).withTimeout(1.0)        bDriverController.onTrue(FeedUpper)        xDriverButton = self.driverController.button(XboxController.Button.kX)        FeedLower = IntakeFeedGamepieceForward(self.intake, motor1speed=-0.5, motor2speed=-0.2).withTimeout(1.5)        xDriverButton.onTrue(FeedLower)        #Operator Controller        leftOperatorBumper = self.operatorController.button(XboxController.Button.kLeftBumper)        leftOperatorBumper.onTrue(InstantCommand(self.elevator.switchUp, self.elevator))        rightOperatorBumper = self.operatorController.button(XboxController.Button.kRightBumper)        rightOperatorBumper.onTrue(InstantCommand(self.elevator.switchDown, self.elevator))        yOperatorButton = self.operatorController.button(XboxController.Button.kY)        singleIntakeCmd = IntakeGamepiece(self.singleIntake, speed=-0.4)        yOperatorButton.whileTrue(singleIntakeCmd)        aOperatorButton = self.operatorController.button(XboxController.Button.kA)        intakeCmd = IntakeGamepiece(self.intake, speed=-0.2)        aOperatorButton.whileTrue(intakeCmd)    def disablePIDSubsystems(self) -> None:        """Disables all ProfiledPIDSubsystem and PIDSubsystem instances.        This should be called on robot disable to prevent integral windup."""    def getAutonomousCommand(self) -> commands2.Command:        """        :returns: the command to run in autonomous        """        command = self.chosenAuto.getSelected()        return command()    def configureAutos(self):        self.chosenAuto = wpilib.SendableChooser()        # you can also set the default option, if needed        self.chosenAuto.setDefaultOption("trajectory example", self.getAutonomousTrajectoryExample)        self.chosenAuto.addOption("Blue 1", self.getBlue1)        self.chosenAuto.addOption("Blue 2", self.getBlue2)        self.chosenAuto.addOption("Blue 3", self.getBlue3)        self.chosenAuto.addOption("Red 1", self.getRed1)        self.chosenAuto.addOption("Red 2", self.getRed2)        self.chosenAuto.addOption("Red 3", self.getRed3)        wpilib.SmartDashboard.putData("Chosen Auto", self.chosenAuto)    def getBlue1(self):        setPipeline = SetCameraPipeline(camera=self.limelight, pipelineIndex=1)        findTag = FindObject(camera=self.limelight, drivetrain=self.robotDrive)        followTag = FollowObject(camera=self.limelight, drivetrain=self.robotDrive, speed=0.3,                                 stopWhen=StopWhen(maxSize=15))        alignTag = AlignWithTag(camera=self.limelight, drivetrain=self.robotDrive, speed=0.2, pushForwardSeconds=0.5,                                pushForwardSpeed=0.2, detectionTimeoutSeconds=2.0)        shootLower = IntakeFeedGamepieceForward(self.intake, motor1speed=-0.5, motor2speed=-0.2).withTimeout(1.5)        command = setPipeline.andThen(findTag).andThen(followTag).andThen(alignTag).andThen(shootLower)        return command    def getBlue2(self):        setPipeline = SetCameraPipeline(camera=self.limelight, pipelineIndex=2)        findTag = FindObject(camera=self.limelight, drivetrain=self.robotDrive)        followTag = FollowObject(camera=self.limelight, drivetrain=self.robotDrive, speed=0.3,                                 stopWhen=StopWhen(maxSize=15))        alignTag = AlignWithTag(camera=self.limelight, drivetrain=self.robotDrive, speed=0.2, pushForwardSeconds=0.5,                                pushForwardSpeed=0.2, detectionTimeoutSeconds=2.0)        shootLower = IntakeFeedGamepieceForward(self.intake, motor1speed=-0.5, motor2speed=-0.2).withTimeout(1.5)        command = setPipeline.andThen(findTag).andThen(followTag).andThen(alignTag).andThen(shootLower)        return command    def getBlue3(self):        setPipeline = SetCameraPipeline(camera=self.limelight, pipelineIndex=3)        findTag = FindObject(camera=self.limelight, drivetrain=self.robotDrive)        followTag = FollowObject(camera=self.limelight, drivetrain=self.robotDrive, speed=0.3,                                 stopWhen=StopWhen(maxSize=15))        alignTag = AlignWithTag(camera=self.limelight, drivetrain=self.robotDrive, speed=0.2, pushForwardSeconds=0.5,                                pushForwardSpeed=0.2,detectionTimeoutSeconds=2.0)        shootLower = IntakeFeedGamepieceForward(self.intake, motor1speed=-0.5, motor2speed=-0.2).withTimeout(1.5)        command = setPipeline.andThen(findTag).andThen(followTag).andThen(alignTag).andThen(shootLower)        return command    def getRed1(self):        setPipeline = SetCameraPipeline(camera=self.limelight, pipelineIndex=4)        findTag = FindObject(camera=self.limelight, drivetrain=self.robotDrive)        followTag = FollowObject(camera=self.limelight, drivetrain=self.robotDrive, speed=0.3,                                 stopWhen=StopWhen(maxSize=15))        alignTag = AlignWithTag(camera=self.limelight, drivetrain=self.robotDrive, speed=0.2, pushForwardSeconds=0.5,                                pushForwardSpeed=0.2, detectionTimeoutSeconds=2.0)        shootLower = IntakeFeedGamepieceForward(self.intake, motor1speed=-0.5, motor2speed=-0.2).withTimeout(1.5)        command = setPipeline.andThen(findTag).andThen(followTag).andThen(alignTag).andThen(shootLower)        return command    def getRed2(self):        setPipeline = SetCameraPipeline(camera=self.limelight, pipelineIndex=5)        findTag = FindObject(camera=self.limelight, drivetrain=self.robotDrive)        followTag = FollowObject(camera=self.limelight, drivetrain=self.robotDrive, speed=0.3,                                 stopWhen=StopWhen(maxSize=15))        alignTag = AlignWithTag(camera=self.limelight, drivetrain=self.robotDrive, speed=0.2, pushForwardSeconds=0.5,                                pushForwardSpeed=0.2, detectionTimeoutSeconds=2.0)        shootLower = IntakeFeedGamepieceForward(self.intake, motor1speed=-0.5, motor2speed=-0.2).withTimeout(1.5)        command = setPipeline.andThen(findTag).andThen(followTag).andThen(alignTag).andThen(shootLower)        return command    def getRed3(self):        setPipeline = SetCameraPipeline(camera=self.limelight, pipelineIndex=6)        findTag = FindObject(camera=self.limelight, drivetrain=self.robotDrive)        followTag = FollowObject(camera=self.limelight, drivetrain=self.robotDrive, speed=0.3,                                 stopWhen=StopWhen(maxSize=15))        alignTag = AlignWithTag(camera=self.limelight, drivetrain=self.robotDrive, speed=0.2, pushForwardSeconds= 0.5)        shootLower = IntakeFeedGamepieceForward(self.intake, motor1speed=-0.5, motor2speed=-0.2).withTimeout(1.5)        command = setPipeline.andThen(findTag).andThen(followTag).andThen(alignTag).andThen(shootLower)        return command    def getAutonomousTrajectoryExample(self) -> commands2.Command:        # Create config for trajectory        config = TrajectoryConfig(            AutoConstants.kMaxSpeedMetersPerSecond,            AutoConstants.kMaxAccelerationMetersPerSecondSquared,        )        # Add kinematics to ensure max speed is actually obeyed        config.setKinematics(DriveConstants.kDriveKinematics)        # An example trajectory to follow. All units in meters.        exampleTrajectory = TrajectoryGenerator.generateTrajectory(            # Start at the origin facing the +X direction            Pose2d(0, 0, Rotation2d(0)),            # Pass through these two interior waypoints, making an 's' curve path            [Translation2d(0.5, 0.5), Translation2d(1, -0.5)],            # End 1.5 meters straight ahead of where we started, facing forward            Pose2d(1.5, 0, Rotation2d(0)),            config,        )        thetaController = ProfiledPIDControllerRadians(            AutoConstants.kPThetaController,            0,            0,            AutoConstants.kThetaControllerConstraints,        )        thetaController.enableContinuousInput(-math.pi, math.pi)        driveController = HolonomicDriveController(            PIDController(AutoConstants.kPXController, 0, 0),            PIDController(AutoConstants.kPXController, 0, 0),            thetaController,        )        swerveControllerCommand = commands2.SwerveControllerCommand(            exampleTrajectory,            self.robotDrive.getPose,  # Functional interface to feed supplier            DriveConstants.kDriveKinematics,            driveController,            self.robotDrive.setModuleStates,            (self.robotDrive,),        )        # Reset odometry to the starting pose of the trajectory.        self.robotDrive.resetOdometry(exampleTrajectory.initialPose())        # Run path following command, then stop at the end.        return swerveControllerCommand.andThen(            cmd.run(                lambda: self.robotDrive.drive(0, 0, 0, False, False),                self.robotDrive,            )        )    def getTestCommand(self) -> typing.Optional[commands2.Command]:        """        :returns: the command to run in test mode (to exercise all systems)        """        return None